always @ (clk)
    begin
        if (state_reg == 2'h0)
        state_next <= 2'h1;
        else if (state_reg == 2'h1)
        state_next <= 2'h2;
        else if (state_reg == 2'h2)
        state_next <= 2'h3;
        else if (state_reg == 2'h3)
        state_next <= 2'h0;
       // else if (state_reg == 2'h3)
       // state_next <= 2'h0;
    end

always @ (user_input, rst_n )
    begin
        if (!rst_n)
        state_next <= 2'h0;
        else if (user_input = 3'h0)
        state_next <= 2'h1;
        else if (user_input = 3'h2)
        state_next <= 2'h2;
        else
        state_next <= 2'h0;
    end

always @ (user_input, state_next, clk )
    begin
        if (!rst_n) {
            state_next <= 2'h0;
        } else if (clk)
        state_next <= 2'h1;
        end
    end

always @ (user_input, state_reg )
    begin
        case (state_reg)
            2'h0: if (state_reg == 2'h0)
                    state_next <= state_reg;
                else
                    state_next <= 2'h0;
            2'h1: if (state_reg == 2'h1)
                    state_next <= state_reg;
                else
                    state_next <= 2'h1;
            2'h2: if (state_reg == 2'h2)
                    state_next <= state_reg;
                else
                    state_next <= 2'h2;
            2'h3: if (state_reg == 2'h3)
                    state_next <= state_reg;
                else
                    state_next <= 2'h3;
        endcase
    end
